mapper.select:
select

from t_agency


    and province = #{province}


    and city = #{city}


    and `name` = #{name}


order by create_time desc
limit #{offset}, #{pageSize}

select

from t_agency


    and province = #{province}


    and city = #{city}


    and type = #{type}


order by create_time desc
limit #{offset}, #{pageSize}

select
    COUNT(1)
from t_agency


    and province = #{province}


    and city = #{city}


    and type = #{type}
